import React, {Component} from 'react';
import {Card, CardImg, CardImgOverlay, CardText,CardBody,CardTitle } from 'reactstrap';



class DishDetail extends Component {

  constructor(props){
    super(props);
  }

  componentDidMount(){
    console.log("componentDidMount is invoked")
  }
  componentDidUpdate(){

  }

  renderComments(comments){
    const commentsConst = comments.map((comment)=>{
      return(
        <div key={comment.id}>
        <ul className="list-unstyled">
          <li className="">{comment.comment}</li>
          <li className="">-- {comment.author},{new Intl.DateTimeFormat('en-US', { year: 'numeric', month: 'short', day: '2-digit'}).format(new Date(Date.parse(comment.date)))}</li>
        </ul>
        </div>
      )
    });
    if(comments!=null){
      return(
        <div>
        <h4>
        Comments
        </h4>
          {commentsConst}
        </div>


      );
    }

    else {
      return(
        <div></div>
      );
    }
  }

  renderDish(dish){
    if (dish!=null) {
      return(
        <div className="container">
          <div className="row ">
            <div className="col-12 col-md-5 m-1">
              <Card>
                <CardImg width="100%" src={dish.image} alt={dish.name}/>
                  <CardBody>
                    <CardTitle>{dish.name}</CardTitle>
                    <CardText>{dish.description}</CardText>
                  </CardBody>
              </Card>
            </div>
            <div className="col-12 col-md-5 m-1">
              {this.renderComments(dish.comments)}
            </div>
          </div>
        </div>

      )
    } else {
      return(<div></div>)
    }
  }


  render() {
    const selectedDish = this.props;
    console.log(selectedDish);
    return (
      this.renderDish(selectedDish.dish)

    );
  }
}


export default DishDetail;
